import { colarg } from "./index.js";
let args = colarg(process.argv.slice(2))
    .option({
    name: "name",
    alias: "n",
    type: "string",
    description: "Some name",
    defaults: "Jeff",
})
    .command("new", "Some Command", (parser) => {
    console.log(parser.option({
        name: "name",
        alias: "n",
        type: "string",
        description: "Name of the project",
        required: true,
    }).help().args);
}).help().args;
console.log(args);
