const COLORS = {
	CLEAR: "\u001b[0m",
	UNDERLINE: "\u001b[4m",
	CYAN: "\u001b[36m",
	BLUE: "\u001b[34m]",
	GREEN: "\u001b[32m",
	RED: "\u001b[31m",
	YELLOW: "\u001b[33m",
	GRAY: "\u001b[90m",
}

const color = (str: string | string[], col : string) => {
	if (Array.isArray(str)) {
		return str.map(x => color(x, col));
	}
	return col + str + COLORS.CLEAR;
}


export {COLORS, color}