interface ArgumentParser {
	get args(): { [key: string]: any };
	command(
		name: string,
		description: string,
		callback: (parser: ArgumentParser, args: any) => void
	): ArgumentParser;
	option({
		name,
		alias,
		type,
		defaults,
		description,
	}: Option): ArgumentParser;
	options(opts: Option[]): ArgumentParser;
	help(): ArgumentParser;
	usage(str: string): ArgumentParser;
}

interface Option {
	name: string;
	alias?: string;
	type?: string;
	defaults?: any;
	description?: string;
	required?: boolean;
	capture?: () => {};
}

export {Option, ArgumentParser}